% set problem valuesA =[1   1   4   ;  0   2   0  ;   1   1   1];       y0=[1 3 0];  % ode45 method:tic  [x, y] = ode45(@(x,y) A*y,[0 1],y0); toc% ode45 uses 12 values of x (given below)%x =   0.00000   0.06813   0.16813   0.26813   0.36813   0.46813   0.56813   0.66813   0.76813   0.86813   0.96813   1.00000   % we may now evaluate sysODE at these 12 valuesticsysODE(A, y0, 0, 0);sysODE(A, y0, 0, 0.06813);sysODE(A, y0, 0, 0.16813);sysODE(A, y0, 0, 0.26813);sysODE(A, y0, 0, 0.36813);sysODE(A, y0, 0, 0.46813);sysODE(A, y0, 0, 0.56813);sysODE(A, y0, 0, 0.66813);sysODE(A, y0, 0, 0.76813);sysODE(A, y0, 0, 0.86813);sysODE(A, y0, 0, 0.96813);sysODE(A, y0, 0, 1.00000);toc% sysODE is slightly faster: 0.017977 < 0.0344479 seconds. But much more similar now (equal number of operations)